static HI_COLOR g_TextColor = 0;

static HI_S32 GetFontIndex(HI_CHAR c)
{
    HI_U32 ii = 0;

    for (ii = 0; ii < sizeof(g_astTextDataInfo) / sizeof(g_astTextDataInfo[0]); ii++)
    {
        if (c == g_astTextDataInfo[ii].u8Char)
        {
            return ii;
        }
    }

    return -1;
}

